(function($) {
  $.fn.fullBg = function(){
        return this.each(function() { 
            var bgImg = $(this);		
            bgImg.addClass('fullBg');
         
            function resizeImg() {
              var $window = $(window);
              var winwidth = $window.width();
              var winheight = $window.height();
              bgImg.css({
                  width: winwidth+'px',
                  height: winheight+'px'
              });		
              /* bgImg.fadeIn(1000); */
            } 
            resizeImg();
            $(window).resize(function() {
              resizeImg();
            }); 
      });
  };
})(jQuery);

